public class Part1 {
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int stopIndex = dna.length();  
        for (int i = startIndex; i < dna.length() - 3; i += 3){
            String substr = dna.substring(i, i+3);
            
            if (substr.equals(stopCodon))
            {
                stopIndex = i;
                break;
            }
        }
        return stopIndex;
    }
    
    
    public String findGene(String dna){
        // find index of the first occurence of start codon ATG
        dna = dna.toUpperCase();
        int index_ATG = dna.indexOf("ATG", 0);
        
        if (index_ATG == -1){
            return "";
        }
        
        int index_TAA = findStopCodon(dna, index_ATG, "TAA");
        int index_TAG = findStopCodon(dna, index_ATG, "TAG");
        int index_TGA = findStopCodon(dna, index_ATG, "TGA");
    
        int closest = Math.min(index_TAA, index_TAG);
        int closest2 = Math.min(closest, index_TGA);
   
        if (closest2 == dna.length()){
            return "";
        }
        
        String finalGene = dna.substring(index_ATG, closest2 + 3);
        return finalGene;
        
    }
    
    
    public void testFindStopCodon(){
        System.out.println("testFindStopCodon");
        
        int res1 = findStopCodon("ATGGTAAACGTCAGT", 0, "GTC");
        System.out.println("Res 1: " + res1);
        
        int res2 = findStopCodon("GTCATGAAACGT", 3, "CGT");
        System.out.println("Res 2: " + res2);
        
        int res3 = findStopCodon("ATGGTA", 0, "GTA");
        System.out.println("Res 3: " + res3);
        
        int res4 = findStopCodon("ATG", 0, "GTA");
        System.out.println("Res4: " + res4);
    }
    
    
    public void testFindGene(){
        
        System.out.println("testFindGene");
        
        // dna with no atg
        String res1 = findGene("GTATAGCGACCCAAA");
        System.out.println("Res 1: " + res1);
        
        // dna with atg and one stop codon
        String res2 = findGene("ATGCCCGGGTAAAAA");
        System.out.println("Res 2: " + res2);
        
        // dna with atg and multiple stop codons
        String res3 = findGene("GTAATGTAATAGAAAGTA");
        System.out.println("Res 3: " + res3);
        
        // dna with atg and no valid stop codon
        String res4 = findGene("ATCATGGTAGCCCGA");
        System.out.println("Res 4: " + res4);
        
    }
    
    public void printAllGenes(String dna){
        int startIndex = 0;
        while (startIndex <= dna.length() - 3){
            String substr = dna.substring(startIndex, dna.length());
            String gene = findGene(substr);
            if (gene != ""){
                System.out.println("Gene: " + gene);
                startIndex += gene.length();
            }else {
                   break;
            }
        }
    }
    
    public StorageResource getAllGenes(String dna){
        StorageResource sr = new StorageResource();
        int startIndex = 0;
        while (startIndex <= dna.length() - 3){
            String substr = dna.substring(startIndex, dna.length());
            String gene = findGene(substr);
            int indexOfGene = dna.indexOf(gene, startIndex);
            if (gene != ""){
                sr.add(gene);
                startIndex = indexOfGene + gene.length();
            }else {
                   break;
            }
        }
        return sr;
    }
    
    public void testGetAllGenes(){
        StorageResource sr = getAllGenes("GTAATGTAATAGAAAGTA");
        for (String g: sr.data()){
            System.out.println(g);
        }
    }
    
    public double cgRatio(String dna){
        int counter = 0;
        double ratio = 0;
        for (int i = 0; i < dna.length(); i++){
            if (dna.charAt(i) == 'C' || dna.charAt(i) == 'G'){
                counter += 1;
            }
        }
        ratio = ((double) counter) / dna.length();
        return ratio;
    }
    
    public int countCTG(String dna){
        int count = 0;
        for (int i = 0; i <= dna.length() - 3; i += 1){
            String substr = dna.substring(i, i+3);
            if (substr.equals("CTG")){
                count += 1;
            }
        }
        return count;
    }
    
    public void processGenes(StorageResource sr){
        // number of strings that are longer than 9 characters
        int numberOfStringsLength = 0;
        int numberOfStringsRatio = 0;
        int howManyGenes = 0;
        String longestGene = "";
        for (String s: sr.data()){
            if (s.length() > 60){
                numberOfStringsLength += 1;
            }
            double cgRatio = cgRatio(s);
            
            if (cgRatio > 0.35){
                numberOfStringsRatio += 1;
            }
            if (s.length() > longestGene.length()){
                longestGene = s;            
            }
            howManyGenes += 1;
        }
        System.out.println(" ");
        System.out.println("Num of strings that are longer than 60 characters: " + numberOfStringsLength);
        System.out.println("Number of strings that have ratio > 0.35: " + numberOfStringsRatio);
        System.out.println("Longest gene length: " + longestGene.length());
        System.out.println("Number of genes: " + howManyGenes);
    }
    
    public void testProcessGenes(){
         
        StorageResource sr = getAllGenes("");
        int ctg_counter = countCTG("");
        System.out.println("Ctg count: " + ctg_counter);
        processGenes(sr);
    }
}
